import React from 'react';
// import connect from '@vkontakte/vkui-connect';
import { View, Alert} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Home from './panels/Home';
import axios from 'axios';


class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'home',
			fetchedUser: null,
			popout: null,
		};
		this.closePopout = this.closePopout.bind(this);
	}

	openDefault(title, msg, actions) {
        this.setState({
            popout:
                <Alert
                    actions={[actions]}
                    onClose={this.closePopout}
                >   
                    <h2>{title}</h2>
                    <p>{msg}</p>
                </Alert>
        });
    }

    closePopout() {
        this.setState({ popout: null });
    }

	createUser = async (id) => {
        fetch('http://localhost/api/createUser?user_id' + id)
            .then((response) => response.json())
            .catch((error) => {
                console.log(error)
            })
    }

    render() {
		return (
			<View popout={this.state.popout} activePanel={this.state.activePanel}>
                <Home id="home" add1={this.add1} fetchedUser={this.state.fetchedUser} />
			</View>
		);
	}

    userBalance = async(id) => {
        const result = await axios.get("http://localhost/api/getUserInfo?user_id=" + id)

    }

    updateUserClicks = async(id) => {
        await axios.get("http://localhost/api/editUserBalance?user_id=" + id)
    }

	add1 = () => {
        this.updateUserClicks(this.state.fetchedUser.id)
	}

}

export default App;
