import React from 'react';
// import connect from '@vkontakte/vkui-connect';
import { View, Alert} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Home from './panels/Home';

class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'home',
			fetchedUser: null,
			popout: null,
            userAcc: null,
            balance: 0
		};
		this.closePopout = this.closePopout.bind(this);
	}

    go = (e) => {
		this.setState({ activePanel: e.currentTarget.dataset.to })
        if(e.currentTarget.dataset.to === "top") {
            this.getUsersTop();
            this.getGroupsTop();
        }
	}

	openDefault(title, msg, actions) {
        this.setState({
            popout:
                <Alert
                    actions={[actions]}
                    onClose={this.closePopout}
                >   
                    <h2>{title}</h2>
                    <p>{msg}</p>
                </Alert>
        });
    }

    closePopout() {
        this.setState({ popout: null });
    }

    updateUserBalance = (id) => {
        fetch(`http://localhost:3000/updateUserBalance?user_id=${id}`)
        .then((response) => {
            this.setState({userAcc: response})
            this.setState({balance: response.balance})
        })
    }
    add1 = () => {
        this.updateUserBalance(this.state.fetchedUser.id)
	}

    render() {
		return (
			<View popout={this.state.popout} activePanel={this.state.activePanel}>
                <Home id="home" fetchedUser={this.state.fetchedUser} go={this.go} balance={this.state.balance} />
			</View>
		);
	}

}

export default App;
