import React from 'react';
import connect from '@vkontakte/vkui-connect';
import { View, Alert} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Home from './panels/Home';
import axios from 'axios'

class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			activePanel: 'home',
			fetchedUser: null,
			popout: null,
            userAcc: null,
            balance: 0
		};
		this.closePopout = this.closePopout.bind(this);
	}

    go = (e) => {
		this.setState({ activePanel: e.currentTarget.dataset.to })
        if(e.currentTarget.dataset.to === "top") {
            this.getUsersTop();
            this.getGroupsTop();
        }
	}

    componentDidMount() {
		connect.subscribe((e) => {
			switch (e.detail.type) {
				case 'VKWebAppGetUserInfoResult':
                    this.setState({ fetchedUser: e.detail.data });
                    console.log(e.detail.data)
					break;
					case 'VKWebAppShowWallPostBoxResult':

  					break;
				default:
					console.log(e.detail.type);
			}
		});
		connect.send('VKWebAppGetUserInfo', {});
        // console.log(window.location)
	}

	openDefault(title, msg, actions) {
        this.setState({
            popout:
                <Alert
                    actions={[actions]}
                    onClose={this.closePopout}
                >   
                    <h2>{title}</h2>
                    <p>{msg}</p>
                </Alert>
        });
    }

    closePopout() {
        this.setState({ popout: null });
    }

    updateUserBalance = (id) => {
        const options = {
            method: "POST",
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: {
                "user_id": id
            },
            url,
        }
        axios(options)
        fetch(`http://localhost:3000/updateUserBalance`, {
            mode: 'no-cors',
            method: "post",
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: {
                "user_id": 233178233
            }
        })
        .then((response) => response.json())
        .then((response) => {
            this.setState({userAcc: response.user})
            this.setState({balance: response.user.balance})
            // console.log(response)
        })
    }

    add1 = () => {
        this.updateUserBalance(this.state.fetchedUser.id)
	}

    render() {
		return (
			<View popout={this.state.popout} activePanel={this.state.activePanel}>
                <Home id="home" fetchedUser={this.state.fetchedUser} go={this.go} balance={this.state.balance} add1={this.add1} />
			</View>
		);
	}

}

export default App;
